import React, { Fragment, useEffect, useState } from "react";


//componentes
import Header from "components/Header/Header.js";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import HeaderLinks from "components/Header/HeaderLinks.js";
import Parallax from "components/Parallax/Parallax.js";
import Icono from '../../components/Icono/Icono.component';
import Cargando from 'components/Cargando/Cargando.component'
import Button from "components/CustomButtons/Button.js";


//JSON
import canales from '../../assets/json/canales.json'

//ANTD
import { Table, Tag, Tooltip } from 'antd';

//PrimeReact
import { addLocale } from 'primereact/api';
import { Calendar } from "primereact/calendar";
import { RadioButton } from 'primereact/radiobutton';

//style
import styles from "assets/jss/material-kit-react/views/profilePage.js";
import classNames from "classnames";
import { makeStyles } from "@material-ui/core/styles";
import './ContratarCita.scss'

//Conexión BBDD
import { urlProfesional } from "configuracion/constantes";
import AxiosConexionConfig from "conexion/AxiosConexionConfig";

//redux
import * as authAction from "../../store/actions/authAction"
import { connect } from "react-redux";
import Modal from "antd/lib/modal/Modal";
import { useHistory } from "react-router";

const useStyles = makeStyles(styles);


const ProfilePageCliente = (props) => {

    const classes = useStyles();
    const [date15, setDate15] = useState(null);
    const [city, setCity] = useState(null);
    const history=useHistory();

    const [visible, setVisible] = React.useState(false);
    const [confirmLoading, setConfirmLoading] = React.useState(false);
    const [modalText, setModalText] = React.useState('');

    const history1 = history.location.search.split("?")[1];
    console.log(history1);

    async function DatosProfesional() {    
      const citasProfesionalURL = "/citas?filter="
      
      const otro = {
        where:{
        idprofesional: is1       
        },  
        include: [{
          relation: "CitaUsuario"
        }]
      }    
     
      try {
        const citasProfesional = await AxiosConexionConfig.get(citasProfesionalURL + encodeURIComponent(JSON.stringify(otro)));
        setCitasPendiente(citasProfesional.data);
  
        if(props.global.usuario){
          setProfesional(props.global.usuario);
        }
        
       
         //console.log(props)
      } catch (e) {
        console.log(e);
      }
    }
  
    const showModal = () => {
      setVisible(true);
    };
  
    const handleOk = () => {
      //setModalText('The modal will be closed after two seconds');
      setConfirmLoading(true);
      setTimeout(() => {

        setVisible(false);
        setConfirmLoading(false);
        history.push('/area-cliente')
      }, 2000);
    };
  
    const handleCancel = () => {
      console.log('Clicked cancel button');
      setVisible(false);
    };

    const ArrayCanales=canales.canales;

    addLocale('es', {
        firstDayOfWeek: 1,
        dayNames: ['domingo', 'lunes', 'martes', 'miércoles', 'jueves', 'viernes', 'sábado'],
        dayNamesShort: ['dom', 'lun', 'mar', 'mié', 'jue', 'vie', 'sáb'],
        dayNamesMin: ['D', 'L', 'M', 'X', 'J', 'V', 'S'],
        monthNames: ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'],
        monthNamesShort: ['ene', 'feb', 'mar', 'abr', 'may', 'jun', 'jul', 'ago', 'sep', 'oct', 'nov', 'dic'],
        today: 'Hoy',
        clear: 'Claro'
      });

      const header = () => {
        return (
          <Header
            color="white"
            brand=""
            rightLinks={<HeaderLinks />}
            fixed
            changeColorOnScroll={{
              height: 200,
              color: "white"
            }}
          />
        )
      }

     const parallax = () => {
        return (
          <Parallax id="sombra" small filter color="headerGreen" >
    
            <div className={classes.container + " headerNameTitle"}>
              <GridContainer justify="flex-end">
    
                <GridItem xs={12} sm={12} md={4}>
                  <h3 className={classes.title + " nameTitle"}>Concertar Cita</h3>
                </GridItem>
    
                <GridItem xs={12} sm={12} md={2}>
                
                </GridItem>
    
              </GridContainer>
            </div>
          </Parallax>
        )}
         
      const body = () => {
        return (
          <div className={classNames(classes.main, classes.mainRaised)}>
            <div>
              <div className={classes.container}>                
    
                <GridContainer>
                  
    
                  <GridItem id="calendario1" xs={12} sm={12} md={6}>
                    <div className="margen"></div>
                    {
                      //poner clase usuarioCliente cuando sea Cliente y cuando sea profesional quitarla para activar o desactivar el css del calendario
                    }
                    <span>Seleccione el día de su cita</span>
                    <Calendar className="usuarioCliente" id="otroId" locale="es" value={date15} onChange={(e) => setDate15(e.value)} minDate={new Date()} disabledDays={[1, 2]} inline />
                  </GridItem>

                  <GridItem xs={12} sm={12} md={6}>
                  <div className="margen"></div>
                    <div>
                    <span>Seleccione el canal de su cita</span>

                   {ArrayCanales.map((canal, index)=>{
                     return(
                      <div key={index} className="p-field-radiobutton">
                        <RadioButton inputId={canal.codigo} name={canal.nombre} value={canal.nombre} onChange={(e) => setCity(e.value)} checked={city === canal.nombre} />                 
                        <Icono id="miIcon" tipo="canal" codigo={canal.nombre} nombre={canal.nombre}></Icono>
                        <label htmlFor="city1">{canal.nombre}</label>
                      </div>
                     )                      
                    })}

                    </div>

                    <Button className="precio" simple color="primary" onClick={showModal} size="lg">
                      <span className="precioText">CONCERTAR CITA</span>
                    </Button> 
                  </GridItem>

                  <div className="margen"></div>
                </GridContainer>
              </div>
            </div>

            <Modal
              title="Title"
              visible={visible}
              onOk={handleOk}
              confirmLoading={confirmLoading}
              onCancel={handleCancel}
            >
              <p>{modalText}</p>
            </Modal>

          </div>
          
          
        )
      }
      return (
        <div>
    
          {header()}
          {parallax()
          }
    
          {props.global !== null ? body() :
    
            <Fragment>
              <div className={classNames(classes.main, classes.mainRaised)}>
                <div>
                  <div className={classes.container}>
                    <Cargando />
                  </div>
                </div>
              </div>
            </Fragment>}
    
        </div>
      );
    }
    
    const mapStateToProps = (rootReducer) => {
      return { global: rootReducer.auth };
    };
    
    export default connect(mapStateToProps, authAction)(ProfilePageCliente);
