import React, { useState, useEffect } from 'react';
import 'antd/dist/antd.css';
import { Form, Input, Button, Space, Select, TimePicker, Row, Col, Tag, InputNumber } from 'antd';
import { MinusCircleOutlined, PlusOutlined } from '@ant-design/icons';
import moment from 'moment';
import canalesJSON from "../../assets/json/canales.json"




const Oferta1 = () => {
    const onFinish = values => {
      console.log('Received values of form:', values);
    };

    const format = 'HH:mm';

    const children = [];
    
    canalesJSON.canales.map((canal)=>{
        children.push(<Tag color='lime' key={canal.nombre}>{canal.nombre}</Tag>);
    })

    function handleChange(value) {
    console.log(`selected ${value}`);
    }

    function onChangeDuracion(value) {
        console.log(`selected ${value}`);
      }

  
    return (
            <Form  layout="vertical" name="dynamic_form_nest_item" onFinish={onFinish} autoComplete="off">
            <Row>
                <Col span={12}>
                    <h4>Oferta</h4>
                    <span>DefiniciÃ³n del servicio</span>
                    <Form.Item
                        label="Tipo de preparaciÃ³n"
                        name="tipoPreparaciÃ³n"
                        rules={[{
                            required: true,
                            message: 'Please input your Tipo de preparaciÃ³n!',
                        },]}
                    >
                        <Input placeholder="Tipo de preparaciÃ³n"/>
                    </Form.Item>

                    <Form.Item
                        name="duracion"
                        label="DuraciÃ³n"
                        rules={[
                        {
                            required: true,
                        },
                        ]}
                    >

                    <Select                    
                        showSearch
                        style={{ width: 200 }}
                        placeholder="DuraciÃ³n"
                        optionFilterProp="children"
                        onChange={onChangeDuracion}

                        filterOption={(input, option) =>
                        option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        <Option value="30m">30 minutos</Option>
                        <Option value="1h">1 hora</Option>
                        <Option value="2h">2 horas</Option>
                        <Option value="3h">3 horas</Option>
                        <Option value="medioDia">Medio dÃ­a</Option>
                        <Option value="diaCompleto">DÃ­a completo</Option>
                        <Option value="semana">Semana</Option>
                        <Option value="mes">Mes</Option>

                    </Select>
                    </Form.Item>

                    <Form.Item
                        name="canales"
                        label="Canales"
                        rules={[
                        {
                            required: true,
                        },
                        ]}
                    >
                    <Select mode="tags" style={{ width: '100%' }} placeholder="Canales" onChange={handleChange}>
                        {children}
                    </Select>
                    </Form.Item>

                    <Form.Item
                        name="tarifa"
                        label="Tarifa"
                        rules={[
                        {
                            required: true,
                        },
                        ]}
                    >
                        <InputNumber
                            defaultValue={1000}
                            formatter={value => `${value} â¬ /`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                            parser={value => value.replace(/\$\s?|(,*)/g, '')}
                            />
                    </Form.Item>

                </Col>

                <Col span={12}>
                    <Form.List name="users">
                    {(fields, { add, remove }) => (
                        <>
                        {fields.map(({ key, name, fieldKey, ...restField }) => (
                            <Space key={key} style={{ display: 'flex', marginBottom: 8 }} align="baseline">
                            <Form.Item
                                {...restField}
                                name={[name, 'dia']}
                                fieldKey={[fieldKey, 'dia']}
                                rules={[{ required: true, message: 'Missing Day' }]}
                            >
                            <Select
                                showSearch
                                style={{ width: 200 }}
                                placeholder="Select a day"
                                optionFilterProp="children"
                                
                                filterOption={(input, option) =>
                                option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                }
                            >
                                <Option value="jack">Jack</Option>
                                <Option value="lucy">Lucy</Option>
                                <Option value="tom">Tom</Option>
                            </Select>
                            </Form.Item>
                            <Form.Item
                                {...restField}
                                name={[name, 'time']}
                                fieldKey={[fieldKey, 'time']}
                                rules={[{ required: true, message: 'Missing Time' }]}
                            >
                            <TimePicker.RangePicker minuteStep={5} defaultValue={moment('12:08', format)} format={format} />
                            
                            </Form.Item>
                            
                            <MinusCircleOutlined onClick={() => remove(name)} />
                            </Space>
                        ))}
                        <Form.Item>
                            <Button type="dashed" onClick={() => add()} block icon={<PlusOutlined />}>
                            Add field
                            </Button>
                        </Form.Item>
                        </>
                    )}
                    </Form.List>
        
                    <Form.Item>
                    <Button type="primary" htmlType="submit">
                        Submit
                    </Button>
                    </Form.Item>
      
                </Col>
        </Row>
        </Form>

    );
  };

export default Oferta1;
