import React, { Fragment } from 'react';
import Flags from 'country-flag-icons/react/3x2';
import { Button, makeStyles, Tooltip } from '@material-ui/core';
import './Icono.style.scss';
import styles from "../../../assets/jss/material-kit-react/tooltipsStyle.js";
import './Icono.style.scss'


const useStyles = makeStyles(styles);

const Icono = (props) => {

  const classes = useStyles();

  const Iconosss = () => {
      switch (props.codigo) {        
        case "ES":
          return (
        
          <Flags.ES id={props.id} className="bandera" />
          
            
          );
        case "GB":
          return (
            <Tooltip
          id="MyToolTip"
          title="Inglés"
          placement={window.innerWidth > 959 ? "bottom" : "left"} 
          classes={{ tooltip: classes.tooltip }} 
        >
          <div className="divIcono">
            <Flags.GB id={props.id} className="bandera" />
            </div>
        </Tooltip>
          );      
        
          case "FR":
          return (   <Tooltip
            id="MyToolTip"
            title="Francés"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            <Flags.FR id={props.id} className="bandera" />
            </div>
        </Tooltip>
          )
          case "DE":
          return (
            <Tooltip
            id="MyToolTip"
            title="Alemán"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            <Flags.DE id={props.id} className="bandera" />
            </div>
        </Tooltip>
          );
        case "SA":
          return (
            <Tooltip
            id="MyToolTip"
            title="Árabe"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            <Flags.SA id={props.id} className="bandera" />
            </div>
        </Tooltip>
          );      
        case "CN":
          return (<Tooltip
            id="MyToolTip"
            title="Chino"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            <Flags.CN id={props.id} className="bandera" />
            </div>
        </Tooltip>
          )
          case "NL":
          return (<Tooltip
            id="MyToolTip"
            title="Holandés"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            
            <Flags.NL id={props.id} className="bandera" />
            </div>
        </Tooltip>
          );
        case "IN":
          return (<Tooltip
            id="MyToolTip"
            title="Hindú"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            <Flags.IN id={props.id} className="bandera" />
            </div>
        </Tooltip>
          );      
        case "IT":
          return (<Tooltip
            id="MyToolTip"
            title="Italiano"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            <Flags.IT id={props.id} className="bandera" />
            </div>
        </Tooltip>
          )
          case "PT":
          return (<Tooltip
            id="MyToolTip"
            title="Portugués"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            <Flags.PT id={props.id} className="bandera" />
            </div>
        </Tooltip>
          );
        case "RU":
          return (<Tooltip
            id="MyToolTip"
            title="Ruso"
            placement={window.innerWidth > 959 ? "bottom" : "left"} 
            classes={{ tooltip: classes.tooltip }} 
          >
            <div className="divIcono">
            <Flags.RU id={props.id} className="bandera" />
            </div>
        </Tooltip>
          );     

          default:
            return (
              <Fragment />
            );   
          }
  }
  return (
    <Tooltip
          id="MyToolTip"
          title={props.nombre}
          placement={window.innerWidth > 959 ? "bottom" : "left"} 
          classes={{ tooltip: classes.tooltip }} 
        >
          <div className="divIcono">
            {Iconosss()}
          </div>
        </Tooltip>
    
    
    );
  }  

  export default Icono;
