import React, { useState, useEffect } from 'react';
import 'antd/dist/antd.css';
import { Form, Input, Button, Space, Select, TimePicker, Row, Col } from 'antd';
import { MinusCircleOutlined, PlusOutlined } from '@ant-design/icons';
import moment from 'moment';



const Oferta1 = () => {
    const onFinish = values => {
      console.log('Received values of form:', values);
    };

    const format = 'HH:mm';

  
    return (
        <Row>
            <Form  layout="vertical" name="dynamic_form_nest_item" onFinish={onFinish} autoComplete="off">
                <Col span={12}>
                    <h4>Oferta</h4>
                    <span>Definici√≥n del servicio</span>
                    <Form.Item label="Field A" tooltip="This is a required field">
                        <Input placeholder="input placeholder" />
                    </Form.Item>
                </Col>

                <Col span={12}>
                    <Form.List name="users">
                    {(fields, { add, remove }) => (
                        <>
                        {fields.map(({ key, name, fieldKey, ...restField }) => (
                            <Space key={key} style={{ display: 'flex', marginBottom: 8 }} align="baseline">
                            <Form.Item
                                {...restField}
                                name={[name, 'dia']}
                                fieldKey={[fieldKey, 'dia']}
                                rules={[{ required: true, message: 'Missing Day' }]}
                            >
                            <Select
                                showSearch
                                style={{ width: 200 }}
                                placeholder="Select a day"
                                optionFilterProp="children"
                                
                                filterOption={(input, option) =>
                                option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                }
                            >
                                <Option value="jack">Jack</Option>
                                <Option value="lucy">Lucy</Option>
                                <Option value="tom">Tom</Option>
                            </Select>
                            </Form.Item>
                            <Form.Item
                                {...restField}
                                name={[name, 'time']}
                                fieldKey={[fieldKey, 'time']}
                                rules={[{ required: true, message: 'Missing Time' }]}
                            >
                            <TimePicker.RangePicker minuteStep={5} defaultValue={moment('12:08', format)} format={format} />
                            
                            </Form.Item>
                            
                            <MinusCircleOutlined onClick={() => remove(name)} />
                            </Space>
                        ))}
                        <Form.Item>
                            <Button type="dashed" onClick={() => add()} block icon={<PlusOutlined />}>
                            Add field
                            </Button>
                        </Form.Item>
                        </>
                    )}
                    </Form.List>
        
                    <Form.Item>
                    <Button type="primary" htmlType="submit">
                        Submit
                    </Button>
                    </Form.Item>
      
                </Col>
            </Form>
        </Row>
    );
  };

export default Oferta1;
