                        />
                    </Form.Item>

                    <Form.Item
                        label="Años de experiencia en RRHH"
                        name="annosExperiencia"
                        rules={[{
                            required: true,
                            message: 'Por favor introduzca los años de experiencia',
                        },]}
                    >
                        <InputNumber name="annosExperiencia" min={0} max={70} placeholder={3} />
                    </Form.Item>

                    <Form.Item
                        label="Tu experiencia (máx. 250 caracteres)"
                        name="experiencia"
                        rules={[{
                            required: true,
                            message: 'Por favor introduzca los años de experiencia',
                        },]}
                    >
                        <TextArea showCount rows={6} maxlength={250} maxLength={250} id="experiencia" name="experiencia" aria-describedby="username2-help" />
                    </Form.Item>

                </Col>

                <Col span={12} className="OfertaCol1">

                    <Form.Item
                        label="Imagen de perfil"
                        name="imagenperfilt"
                    >
                        <ImgCrop rotate>
                            <Upload
                                fileList={fileList}
                                onChange={onChange}
                                onPreview={onPreview}
                                listType="picture-card"
                                name="imagenperfilt"
                                id="imagenperfilt"

                            >
                                {fileList.length < 1 && '+ Adiciona tu imagen de perfil'}
                            </Upload>
                        </ImgCrop>
                    </Form.Item>

                    <Form.Item
                        label="Imagen de perfil"
                        name="imagenperfilt"
                    >
                        <Select mode="tags" style={{ width: '100%' }} placeholder="Tags Mode" onChange={(e) => setSelectedSectores(e.value)}>
                            {children}
                        </Select>
                    </Form.Item>


                    <div className=" p-col-12">
                        <label htmlFor={"sectores"} className="text textMarca">Sectores</label>
                        <span className="p-fluid">
                            <AutoComplete value={selectedSectores} suggestions={filteredSectores} completeMethod={searchSector} field="name" multiple onChange={(e) => setSelectedSectores(e.value)} />
                        </span>

                    </div>

                    <div className="p-col-12">
                        <label htmlFor={"perfiles"} className="text textMarca">Perfiles</label>
                        <div>
                            <Input id="perfiles" name="perfiles" type="text" className="with100" />
